mapper.select:

select 		
p_price,p_price*0.95 from product

where p_price*0.95 >= #{0}
and p_price*0.95 <= #{1} 

order by p_price asc

select p_id,p_name,p_price,p_canBuy,p_canRent,p_soldCount, p_category,p_mainImg,p_subImg,p_regdate from product
where p_name LIKE CONCAT('%', #{searchType},'%')
or p_name LIKE CONCAT('%', #{searchType},'%') or p_id LIKE CONCAT('%', #{searchType},'%') or p_category LIKE CONCAT('%', #{searchType},'%') ORDER BY p_id desc LIMIT #{pageStart}, #{perPageNum}

select review.v_id, member.m_img, review.v_pid, review.v_mid, review.v_content, review.v_like, review.v_date from review,member WHERE v_pid = #{param2}	and v_mid = m_id
ORDER BY v_id desc
limit #{param1.pageStart}, #{param1.perPageNum}

select review.v_id, member.m_img, review.v_pid, review.v_mid, review.v_content,review.v_like, review.v_date from review,member WHERE v_pid = #{param2}	and v_mid = m_id
ORDER BY v_id desc
	limit #{param1.pageStart}, #{param1.perPageNum}
