mapper.select:
select p_id,p_name,p_price,p_canBuy,p_canRent,p_soldCount, p_category,p_mainImg,p_subImg,p_regdate from product
where p_name LIKE CONCAT('%', #{searchType},'%')
or p_name LIKE CONCAT('%', #{searchType},'%') or p_id LIKE CONCAT('%', #{searchType},'%') or p_category LIKE CONCAT('%', #{searchType},'%') ORDER BY p_id desc LIMIT #{pageStart}, #{perPageNum}
